{  
   "pages":[  
      {  
         "name":"WelcomePage",
         "elements":[  
            {  
               "type":"panel",
               "name":"WelcomePanel",
               "elements":[  
                  {  
                     "type":"html",
                     "name":"question1",
                     "html":"Thanks for taking our survey to assess how manufacturers were affected, and responded, to the impact of Hurricane Harvey. To begin with, we'll tailor the questions to only those areas that affected you.<p>Check all that apply:"
                  },
                  {  
                     "type":"checkbox",
                     "name":"WhichCategories",
                     "title":"Which categories of your manufacturing business activities were directly and materially affected by Hurricane Harvey?",
                     "choices":[  
                        "Facilities",
                        "Production",
                        "Equipment",
                        "Inventory",
                        "People",
                        "Office",
                        {  
                           "value":"Sales",
                           "text":"Sales/Supply Chain"
                        },
                        "Capital",
                        "Mitigation",
                        "Revenue"
                     ],
                     "colCount":3
                  }
               ],
               "title":"Welcome!"
            }
         ]
      },
      {  
         "name":"FacilitiesPage",
         "elements":[  
            {  
               "type":"text",
               "name":"HowManyFacilities",
               "width":"3",
               "title":"How many independent facilities (i.e. freestanding buildings) does your company occupy?",
               "defaultValue":1,
               "isRequired":true,
               "requiredErrorText":"If over 99, use 99",
               "inputType":"number",
               "size":""
            },
            {  
               "type":"text",
               "name":"PctBuildingsAffected",
               "title":"What percentage of the answer above suffered impactful damage due to Harvey?"
            },
            {  
               "type":"multipletext",
               "name":"Damage",
               "title":"For each category below, enter the percentage of buildings that were impacted by:",
               "items":[  
                  {  
                     "name":"Water",
                     "title":"Flood water ",
                     "validators":[  
                        {  
                           "type":"answercount"
                        },
                        {  
                           "type":"numeric",
                           "minValue":0,
                           "maxValue":100
                        }
                     ]
                  },
                  {  
                     "name":"Rain",
                     "title":"Rainfall or hail damage "
                  },
                  {  
                     "name":"FallingDebris",
                     "title":"Falling debris"
                  },
                  {  
                     "name":"Wind",
                     "title":"Wind "
                  },
                  {  
                     "name":"Fire"
                  },
                  {  
                     "name":"Vandalism"
                  },
                  {  
                     "name":"Other",
                     "inputType":"number"
                  }
               ]
            },
            {  
               "type":"radiogroup",
               "name":"UtilizeAll",
               "title":"Is your company utilizing all of the damaged buildings today?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"text",
               "name":"PctNotUsed",
               "visible":false,
               "visibleIf":"{UtilizeAll} = 'No'",
               "title":"What percentage are still NOT being used?",
               "inputType":"number",
               "placeHolder":"0"
            },
            {  
               "type":"multipletext",
               "name":"Damage",
               "title":"What is your estimate of monetary facility damage, or cost of repair, in the following categories:",
               "items":[  
                  {  
                     "name":"Physical",
                     "title":"Physical integrity of building proper (roof, walls, floor)"
                  },
                  {  
                     "name":"Biological",
                     "title":"Biological infestation (mold, algae, etc)"
                  },
                  {  
                     "name":"Environmental",
                     "title":"Environmental surface damage (carpeting, painted surfaces, etc)"
                  }
               ]
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'Facilities'",
         "title":"Facilities"
      },
      {  
         "name":"ProductionPage",
         "elements":[  
            {  
               "type":"radiogroup",
               "name":"ProductionLoss",
               "title":"Did your company suffer losses due to loss of production? ",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"checkbox",
               "name":"WhichLossOfProduction",
               "visible":false,
               "visibleIf":"{ProductionLoss}='Yes'",
               "title":"Check all of the following factors that contributed to your loss of production",
               "choices":[  
                  {  
                     "value":"Electricity",
                     "text":"Electricity failure"
                  },
                  {  
                     "value":"Gas",
                     "text":"Natural gas outage"
                  },
                  {  
                     "value":"Water",
                     "text":"Lack of potable water"
                  },
                  {  
                     "value":"Internet",
                     "text":"Loss of internet service"
                  },
                  {  
                     "value":"Workspace",
                     "text":"Damage of work spaces"
                  }
               ],
               "colCount":3
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'Production'",
         "title":"Production"
      },
      {  
         "name":"EquipmentPage",
         "elements":[  
            {  
               "type":"text",
               "name":"CostOfEquipmentRepair",
               "title":"What was your cost to repair or replace damaged equipment?"
            },
            {  
               "type":"checkbox",
               "name":"EquipmentDamageDueTo",
               "title":"Was there substantial damage from:",
               "choices":[  
                  "Water",
                  "Wind",
                  "Hail",
                  "Fire",
                  {  
                     "value":"FallingObjects",
                     "text":"Falling objects"
                  },
                  {  
                     "value":"Maintenance",
                     "text":"Lack of maintenance or operation during storm"
                  }
               ],
               "colCount":2
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'Equipment'",
         "title":"Equipment"
      },
      {  
         "name":"InventoryPage",
         "elements":[  
            {  
               "type":"text",
               "name":"RevenueLoss",
               "title":"How much revenue was lost due to destroyed manufactured inventory? "
            },
            {  
               "type":"checkbox",
               "name":"ReasonForLoss",
               "title":"What were the cause(s) of this loss?",
               "choices":[  
                  "Water",
                  "Wind",
                  "Fire",
                  {  
                     "value":"FallingObjects",
                     "text":"Falling Objects"
                  },
                  "Other"
               ],
               "colCount":2
            },
            {  
               "type":"text",
               "name":"SupplyInventoryLoss",
               "title":"How much will it cost to replace the supply inventory lost due to Harvey?"
            },
            {  
               "type":"checkbox",
               "name":"SupplyInventoryLossCauses",
               "title":"What were the cause(s) of the supply inventory loss?",
               "choices":[  
                  "Water",
                  "Wind",
                  "Fire",
                  {  
                     "value":"FallingObjects",
                     "text":"Falling Objects"
                  },
                  "Other"
               ],
               "colCount":2
            },
            {  
               "type":"text",
               "name":"RepairCost",
               "title":"If you were able to reclaim damaged inventory, what is the cost of the repairs?"
            },
            {  
               "type":"text",
               "name":"LossDueToEqptFailure",
               "title":"What is the cost (if any) of inventory lost due to equipment failure (e.g. HVAC or refrigeration)?"
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'Inventory'",
         "title":"Inventory"
      },
      {  
         "name":"PeoplePage",
         "elements":[  
            {  
               "type":"radiogroup",
               "name":"BusinessClosed",
               "title":"Were you forced to physically close your business after Harvey for one or more days?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"text",
               "name":"EmployeeDaysLost",
               "title":"How many total employee-days were lost at your business, for any Harvey-related reason?"
            },
            {  
               "type":"text",
               "name":"PctDaysLostDueToConditions",
               "title":"What percentage of employee-days were lost due to conditions at your facilities? "
            },
            {  
               "type":"text",
               "name":"PctDaysLostInfrastructure",
               "title":"What percentage of employee-days were lost due to lack of transportation infrastructure? "
            },
            {  
               "type":"text",
               "name":"PctDaysLostEmployeesHomes",
               "title":"What percentage of employee-days were lost due to conditions at employees homes? "
            },
            {  
               "type":"checkbox",
               "name":"HowCommunicateWithEmployees",
               "title":"How were you able to communicate with your employees during this time?",
               "choices":[  
                  {  
                     "value":"Phone",
                     "text":"By phone"
                  },
                  {  
                     "value":"Internet",
                     "text":"By internet"
                  }
               ]
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'People'",
         "title":"People"
      },
      {  
         "name":"OfficePage",
         "elements":[  
            {  
               "type":"radiogroup",
               "name":"WasOfficeEquipmentDamaged",
               "title":"Did you have office equipment damaged by the storm?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"text",
               "name":"CostToRepairOrReplace",
               "title":"What was the cost to repair or replace office equipment?"
            },
            {  
               "type":"text",
               "name":"DaysInternetOut",
               "title":"For how many days were you without internet connectivity at your office?"
            },
            {  
               "type":"text",
               "name":"DaysWithoutPhone",
               "title":"For how many days were you without phone service at your office?"
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'Office'",
         "title":"Office"
      },
      {  
         "name":"SalesPage",
         "elements":[  
            {  
               "type":"radiogroup",
               "name":"DidSufferLosses",
               "title":"Did you suffer losses due to inability to receive necessary supplies or ship sales?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"text",
               "name":"LossesDueToSupplies",
               "visibleIf":"{DidSufferLosses} = 'Yes'",
               "title":"Loss of production due to unavailable supplies "
            },
            {  
               "type":"text",
               "name":"LossesDueToShipping",
               "visibleIf":"{DidSufferLosses} = 'Yes'",
               "title":"Loss of sales due to inability to ship product "
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'Sales'",
         "title":"Sales/Supply Chain"
      },
      {  
         "name":"CapitalPage",
         "elements":[  
            {  
               "type":"radiogroup",
               "name":"WasAdequatelyCapitalized",
               "title":"Prior to Harvey, do you feel your business was adequately capitalized to recover from a regional disaster?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"radiogroup",
               "name":"DidNeedLoan",
               "title":"Did you require a loan to help recover from the impact of Harvey?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"radiogroup",
               "name":"AmtOfGovtLoan",
               "visibleIf":"{DidNeedLoan} = 'Yes'",
               "title":"Were you able to secure a government loan subsequent to Harvey?",
               "choices":[  
                  "No",
                  {  
                     "value":"Under10k",
                     "text":"Less than $10,000"
                  },
                  {  
                     "value":"Under100k",
                     "text":"$10,001-$100,000"
                  },
                  {  
                     "value":"Under1M",
                     "text":"$100,001-$1,000,000"
                  },
                  {  
                     "value":"Under10M",
                     "text":"$1,000,001-$10,000,000"
                  },
                  {  
                     "value":"Over10M",
                     "text":"Over $10,000,000"
                  }
               ]
            },
            {  
               "type":"radiogroup",
               "name":"PrivateEquityLoan",
               "visibleIf":"{DidNeedLoan} = 'Yes'",
               "title":"Were you able to secure a private equity loan subsequent to Harvey?",
               "choices":[  
                  "No",
                  {  
                     "value":"Under10k",
                     "text":"Less than $10,000"
                  },
                  {  
                     "value":"Under100k",
                     "text":"$10,001-$100,000"
                  },
                  {  
                     "value":"Under1M",
                     "text":"$100,001-$1,000,000"
                  },
                  {  
                     "value":"Under10M",
                     "text":"$1,000,001-$10,000,000"
                  },
                  {  
                     "value":"Over10M",
                     "text":"Over $10,000,000"
                  }
               ]
            },
            {  
               "type":"radiogroup",
               "name":"BankLoan",
               "visibleIf":"{DidNeedLoan} = 'Yes'",
               "title":"Were you able to secure a loan from a public lending agency (e.g. bank)?",
               "choices":[  
                  "No",
                  {  
                     "value":"Under10k",
                     "text":"Less than $10,000"
                  },
                  {  
                     "value":"Under100k",
                     "text":"$10,001-$100,000"
                  },
                  {  
                     "value":"Under1M",
                     "text":"$100,001-$1,000,000"
                  },
                  {  
                     "value":"Under10M",
                     "text":"$1,000,001-$10,000,000"
                  },
                  {  
                     "value":"Over10M",
                     "text":"Over $10,000,000"
                  }
               ]
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'Capital'",
         "title":"Capital"
      },
      {  
         "name":"MitigationPage",
         "elements":[  
            {  
               "type":"radiogroup",
               "name":"ContingencyPlans",
               "title":"Prior to Harvey, did your company have contingency plans in place in the event of a natural or man-made disaster?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"radiogroup",
               "name":"PeoplePrepared",
               "title":"Where applicable, were your people prepared to continue the business working from extramural locations via internet or phone?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"radiogroup",
               "name":"SecureComputing",
               "title":"Was your computing infrastructure secured prior to Harvey? ",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"radiogroup",
               "name":"SecureData",
               "title":"Was your data secured prior to Harvey?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"radiogroup",
               "name":"SecureDataOffsite",
               "visibleIf":"{SecureData} = 'Yes'",
               "title":"Was the data secured off-site?",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"radiogroup",
               "name":"LostData",
               "title":"Did you lose data that adversely affected you ability to conduct business? ",
               "choices":[  
                  "Yes",
                  "No"
               ]
            },
            {  
               "type":"comment",
               "name":"EffectOfLostData",
               "visibleIf":"{LostData} = 'Yes'",
               "title":"How was your business affected by this data loss?"
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'Mitigation'",
         "title":"Mitigation"
      },
      {  
         "name":"RevenuePage",
         "elements":[  
            {  
               "type":"text",
               "name":"question2",
               "title":"What would you estimate is the percentage of the total loss of revenue to your business compared to the period preceding Harvey (e.g. 100% = lost all business, 0% = no business interruption at all):"
            },
            {  
               "type":"text",
               "name":"question3",
               "title":"What percentage of this lost business was due to impact at your facilities?"
            },
            {  
               "type":"text",
               "name":"question4",
               "title":"What percentage of this lost business was due to Harvey’s impact on your customers?"
            }
         ],
         "visible":false,
         "visibleIf":"{WhichCategories} contains 'Revenue'",
         "title":"Revenue"
      }
